CREATE FUNCTION [dbo].[clean_str](@str nvarchar(max))
RETURNS nvarchar(max)
    AS 
        BEGIN

            DECLARE @cleaned_str AS NVARCHAR(MAX)
			
			SET @cleaned_str = IIF(@str IS NULL, N'', @str)
			SET @cleaned_str = REPLACE(@cleaned_str, N'''', N'''''');
        
            RETURN @cleaned_str
        END
GO
/****** Object:  UserDefinedFunction [dbo].[is_diff]    Script Date: 19/02/2026 17:07:14 ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO
CREATE FUNCTION [dbo].[is_diff](@str nvarchar(max), @str2 nvarchar(max))
RETURNS tinyint
    AS 
        BEGIN
			DECLARE @parse_str AS tinyint = IIF(ISNULL(@str, '') = '', 1, 0)
			DECLARE @parse_str2 AS tinyint = IIF(ISNULL(@str2, '') = '', 1, 0)

            RETURN IIF(@parse_str <> @parse_str2 OR REPLACE(REPLACE(REPLACE(@str, CHAR(13), ''), CHAR(10), ''),'&#x0D;','') <> REPLACE(REPLACE(REPLACE(@str2, CHAR(13), ''), CHAR(10), ''),'&#x0D;',''), 1, 0)
        END

