/****** Object:  UserDefinedFunction [dbo].[is_diff_num]    Script Date: 19/02/2026 17:07:14 ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO
CREATE FUNCTION [dbo].[is_diff_num](@i int, @i2 int)
RETURNS tinyint
    AS 
        BEGIN
			DECLARE @i_is_empty tinyint = IIF(ISNULL(@i, 0) = 0, 1, 0)
			DECLARE @i2_is_empty tinyint = IIF(ISNULL(@i2, 0) = 0, 1, 0)

            RETURN IIF(@i_is_empty <> @i2_is_empty OR @i <> @i2, 1, 0)
        END

