                         AS שם, dbo.אלפון_כללי.רחוב + N' ' + COALESCE (dbo.אלפון_כללי.[מס רחוב], N'') AS כתובת, MONTH(dbo.אלפון_כללי.ת_לידה) AS [חודש לידה לועזי], DAY(dbo.אלפון_כללי.ת_לידה) AS [יום לידה לועזי], YEAR(dbo.אלפון_כללי.ת_לידה) 
                         AS [שנה לידה לועזי], dbo.קבוצות.סיווג_קבוצה,
                         CASE WHEN ישוב_לדיוור is null THEN ישוב ELSE ישוב_לדיוור END AS [ישוב לדיוור],
					     CASE WHEN ארץ_לדיוור is null THEN ארץ ELSE ארץ_לדיוור END AS [ארץ לדיוור],
						 CASE WHEN מיקוד_לדיוור is null THEN מיקוד ELSE מיקוד_לדיוור END AS [מיקוד לדיוור],
						 CASE WHEN תואר_לדיוור is null THEN תואר_לפני ELSE תואר_לדיוור END AS [תואר לדיוור],
						 CASE WHEN שם_לדיוור is null THEN ארץ ELSE שם_לדיוור END AS [שם לדיוור],
						 CASE WHEN כתובת_לדיוור is null THEN ארץ ELSE כתובת_לדיוור END AS [כתובת לדיוור]
FROM            dbo.אלפון_כללי INNER JOIN
                         dbo.סוגי_כרטיסים ON dbo.אלפון_כללי.סוג_כרטיס = dbo.סוגי_כרטיסים.קוד_סוג LEFT OUTER JOIN
                         dbo.קבוצות ON dbo.אלפון_כללי.קבוצה = dbo.קבוצות.קוד_קבוצה
WHERE        ((SELECT        סוג
                            FROM            dbo.DO
                            WHERE        (REPLACE(UserName, ' ', '_') = SUSER_NAME())) = 1) OR
                         (CHARINDEX('\', SUSER_NAME()) > 0) OR
                         (dbo.אלפון_כללי.קבוצה IN
                             (SELECT        קוד_קבוצה
                               FROM            dbo.קבוצות_משתמשים
                               WHERE        (REPLACE(משתמש, ' ', '_') = SUSER_NAME())))
