CREATE VIEW [dbo].[ReportMaster]
AS
SELECT        [מס סידורי], תנועות.מס_תנועה, Format([פרטי תנועות].[סכום], NCHAR(8234) + [מטבע].[סמל] + ' #,##0.00' + NCHAR(8236) + ';' + NCHAR(8234) + [מטבע].[סמל] + ' -#,##0.00' + NCHAR(8236)) AS [סך תנועה], IIf([פרטי תנועות].[סכום] < 0, 
                         Format([סכום_חוז], NCHAR(8234) + Mt.[סמל] + ' #,##0.00' + NCHAR(8236) + ';' + NCHAR(8234) + Mt.[סמל] + ' -#,##0.00' + NCHAR(8236)), NULL) AS חובה, IIf([פרטי תנועות].[סכום] > 0, Format([סכום_חוז], NCHAR(8234) 
                         + Mt.[סמל] + ' #,##0.00' + NCHAR(8236) + ';' + NCHAR(8234) + Mt.[סמל] + ' -#,##0.00' + NCHAR(8236)), NULL) AS זכות, Payment.Payment AS אמצעי, [קודי פעולה].תיאור_פעולה AS [סוג פעולה], [פרטי תנועות].מס_תשלום AS תש, 
