FROM            ((((([פרטי תנועות] INNER JOIN
                         תנועות ON [פרטי תנועות].[מס תנועה] = תנועות.מס_תנועה) INNER JOIN
                         Payment ON תנועות.[אמצעי תשלום] = Payment.IDPayment) INNER JOIN
                         [קודי פעולה] ON תנועות.קוד_פעולה = [קודי פעולה].[קוד פעולה]) INNER JOIN
                         תיקים ON תנועות.מס_תיק = תיקים.מס_תיק) INNER JOIN
                         מטבע ON תנועות.מטבע = מטבע.[קוד מטבע]) INNER JOIN
                         מטבע AS Mt ON תיקים.קוד_מטבע = Mt.[קוד מטבע]


