AS
SELECT התחייבויות.מס_התחייבות AS קוד_התחייבות, התחייבויות.סכום*-1 AS [סך התחייבות], Sum(IIf(התחייבויות.מטבע=0,תנועות.סכום_בשח,תנועות.סכום)) AS [סך תנועה], Sum(התחייבויות_תנועות.סך_ניכוי) AS [תשלום תנועות], [התחייבויות].[סכום]*-1+Sum(COALESCE([סך_ניכוי],0)) AS יתרה, [התחייבויות].[סכום]*-1+Sum(IIf([התחייבויות].[מטבע]=0,COALESCE([תנועות].[סהכ_גביה_שח],0),COALESCE([תנועות].[סהכ_גביה],0))) AS יתרה_עדכנית
FROM (התחייבויות_תנועות RIGHT JOIN התחייבויות ON התחייבויות_תנועות.קוד_התחייבות = התחייבויות.מס_התחייבות) LEFT JOIN תנועות ON התחייבויות_תנועות.מס_תנועה = תנועות.מס_תנועה
GROUP BY התחייבויות.מס_התחייבות, התחייבויות.סכום*-1


