	+ N' [ת_לידה] = ' +				'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[ת_לידה])			+ '''' + ', '
	+ N' [תאריך_לידה_עברי] = ' +	'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[תאריך_לידה_עברי])	+ '''' + ', '
	+ N' [שם משפחה] = ' +			'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[שם משפחה])			+ '''' + ', '
	+ N' [רחוב] = ' +				'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[רחוב])				+ '''' + ', '
	+ N' [מס רחוב] = ' +			'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[מס רחוב])			+ '''' + ', '
	+ N' [כניסה] = ' +				'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[כניסה])				+ '''' + ', '
	+ N' [דירה] = ' +				'N''' + הגמח_המרכזי	.dbo.clean_str(L_Y.[דירה])				+ '''' + ', '
	+ N' [קומה] = ' +				'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[קומה])				+ '''' + ', '
	+ N' [ישוב] = ' +				'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[ישוב])				+ '''' + ', '
	+ N' [מס_ילדים] = ' +			'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[מס_ילדים])			+ '''' + ', '
	+ N' [שם_אב] = ' +				'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[שם_אב])				+ '''' + ', '
	+ N' [שם_אם] = ' +				'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[שם_אם])				+ '''' + ', '
	+ N' [שם_ב_זוג] = ' +			'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[שם_ב_זוג])			+ '''' + ' '
	+ N' WHERE ID = ' +  CAST(L_Y.ID AS varchar(255)) + ';'
    from        [SQL-SRV].[Flt_Gemach].dbo.[Alfon] R_Y
	inner join  הגמח_המרכזי.dbo.[Alfon] L_Y
	on          R_Y.ID = L_Y.ID
	WHERE R_Y.ID = 1
	AND
	R_Y.[שם לקוח]			collate Hebrew_CI_AS	<> L_Y.[שם לקוח]  OR
	R_Y.[ת_זהות]			collate Hebrew_CI_AS	<> L_Y.[ת_זהות] OR
	R_Y.[ת_לידה]									<> L_Y.[ת_לידה] OR
	R_Y.[תאריך_לידה_עברי]	collate Hebrew_CI_AS	<> L_Y.[תאריך_לידה_עברי] OR
	R_Y.[שם משפחה]			collate Hebrew_CI_AS	<> L_Y.[שם משפחה] OR
	R_Y.[רחוב]				collate Hebrew_CI_AS	<> L_Y.[רחוב] OR
	R_Y.[מס רחוב]			collate Hebrew_CI_AS	<> L_Y.[מס רחוב] OR
	R_Y.[כניסה]				collate Hebrew_CI_AS	<> L_Y.[כניסה] OR
	R_Y.[דירה]				collate Hebrew_CI_AS	<> L_Y.[דירה] OR
	R_Y.[קומה]				collate Hebrew_CI_AS	<> L_Y.[קומה] OR
	R_Y.[ישוב]				collate Hebrew_CI_AS	<> L_Y.[ישוב] OR
	R_Y.[מס_ילדים]									<> L_Y.[מס_ילדים] OR
	R_Y.[שם_אב]				collate Hebrew_CI_AS	<> L_Y.[שם_אב] OR
	R_Y.[שם_אם]				collate Hebrew_CI_AS	<> L_Y.[שם_אם] OR
	R_Y.[שם_ב_זוג]			collate Hebrew_CI_AS	<> L_Y.[שם_ב_זוג]
    FOR XML path('')
)

