IF NOT @xml IS NULL
    BEGIN
	SET @sql = 'set nocount on;' + cast(@xml AS VARCHAR(max)) + 'set nocount off;'
	EXEC (@sql) AT [SQL-SRV]
	END
     



DECLARE @m int = (SELECT  MAX(R_Y.ID)
     FROM [SQL-SRV].[Flt_Gemach].dbo.[Alfon] R_Y)


DECLARE @xml_insert XML

SET @xml_insert = (
    SELECT TOP 10000 N'insert into [SQL-SRV].[Flt_Gemach].dbo.[Alfon] (' +
	N'[ID], ' +
	N'[שם לקוח], ' +
	N'[ת_זהות], ' +
	N'[ת_לידה], ' +
	N'[תאריך_לידה_עברי], ' +
	N'[שם משפחה], ' +
	N'[שם פרטי], ' +
	N'[רחוב], ' +
	N'[מס רחוב], ' +
	N'[כניסה], ' +
	N'[דירה], ' +
	N'[קומה], ' +
	N'[ישוב], ' +
	N'[מס_ילדים], ' +
	N'[שם_אב], ' +
	N'[שם_אם], ' +
	N'[שם_ב_זוג]' +
	N') values ('
	+ 'N''' +  הגמח_המרכזי.dbo.clean_str(L_Y.[ID]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[שם לקוח]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[ת_זהות]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[ת_לידה]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[תאריך_לידה_עברי]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[שם משפחה]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[שם פרטי]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[רחוב]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[מס רחוב] ) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[כניסה]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[דירה]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[קומה]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[ישוב]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[מס_ילדים]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[שם_אב]) + '''' + ','
	+ 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[שם_אם]) + '''' + ','
    + 'N''' + הגמח_המרכזי.dbo.clean_str(L_Y.[שם_ב_זוג]) + '''' + ');'
    FROM הגמח_המרכזי.dbo.[Alfon] L_Y
	WHERE L_Y.[ID] > @m OR @m IS NULL
	ORDER BY L_Y.[ID] ASC
    FOR XML path('')
)


DECLARE @sql_insert AS NVARCHAR(max)
IF NOT @xml_insert IS NULL
    BEGIN

	SET @sql_insert = 'set nocount on;' + cast(@xml_insert AS NVARCHAR(max)) + 'set nocount off;'
	EXEC (@sql_insert) AT [SQL-SRV]
	END

END
